import type { SoundEffect } from '../types';

// Sound assets are encoded in Base64 to avoid needing separate files.
const sounds = {
  click: 'data:audio/mpeg;base64,-A',
  complete: 'data:audio/mpeg;base64,UklGRkQDAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQADAAD/AP8A/wD/APMA9QDzAP8A9QDvAPwA8QDyAPQA7wDuAO0A6gDqAOoA6ADiANkA0wDPANUA2ADcANsA3QDeANwA2gDPAMgAwQDJAM8A1wDXANwA2QDTA     -A',
  add: 'data:audio/mpeg;base64,UklGRkQDAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQADAAD/AP8A/wD/APMA9QDzAP8A9QDvAPwA8QDyAPQA7wDuAO0A6gDqAOoA6ADiANkA0wDPANUA2ADcANsA3QDeANwA2gDPAMgAwQDJAM8A1wDXANwA2QDTA     ',
'delete': 'data:audio/mpeg;base64,UklGRkQDAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQADAAD/AP8A/wD/APMA9QDzAP8A9QDvAPwA8QDyAPQA7wDuAO0A6gDqAOoA6ADiANkA0wDPANUA2ADcANsA3QDeANwA2gDPAMgAwQDJAM8A1wDXANwA2QDTA     -A',
  login: 'data:audio/wav;base64,UklGRkQDAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQADAAD/AP8A/wD/APMA9QDzAP8A9QDvAPwA8QDyAPQA7wDuAO0A6gDqAOoA6ADiANkA0wDPANUA2ADcANsA3QDeANwA2gDPAMgAwQDJAM8A1wDXANwA2QDTA     -A',
};

// A pool of audio elements to reuse, preventing memory leaks and performance issues.
const audioPool: { [key in SoundEffect]?: HTMLAudioElement[] } = {};
const POOL_SIZE = 3;

// Pre-load audio elements into the pool
Object.keys(sounds).forEach(key => {
    const soundKey = key as SoundEffect;
    audioPool[soundKey] = [];
    for (let i = 0; i < POOL_SIZE; i++) {
        const audio = new Audio(sounds[soundKey]);
        audio.preload = 'auto';
        audioPool[soundKey]?.push(audio);
    }
});

let currentAudioIndex: { [key in SoundEffect]?: number } = {};

export const playSound = (sound: SoundEffect) => {
    try {
        const pool = audioPool[sound];
        if (!pool) return;
        
        let index = currentAudioIndex[sound] || 0;
        
        // Find an available audio element
        if(!pool[index].paused){
            index = (index + 1) % POOL_SIZE;
        }

        const audio = pool[index];
        audio.currentTime = 0;
        audio.play().catch(error => {
            // Autoplay was prevented, which is common in browsers.
            // We can ignore this error silently as sounds are non-critical.
        });
        
        currentAudioIndex[sound] = (index + 1) % POOL_SIZE;

    } catch (error) {
        console.error(`Could not play sound: ${sound}`, error);
    }
};